"let url = data.env.MS_TEAMS_WEBHOOK_URL;

  let catalogItemName = data.request.body.catalogItemName;
  let catalogItemId = data.request.body.catalogItemId;
  let catalogItemUrl = data.env.API_CENTRAL_URL+"/catalog/explore"+"/"+catalogItemId;

  let subscriptionName = data.request.body.subscriptionName;
  let subscriptionId = data.request.body.subscriptionId;
  let subscriptionUrl = data.env.API_CENTRAL_URL+"/catalog"+"/"+catalogItemId+"/subscriptions"+"/"+subscriptionId;

  let requestorName = data.request.body.requestorName;
  // let requestorUrl = "https://platform.axway.com/user/"+data.request.body.payload.subscription.metadata.createUserId;
  let requestorUrl = "https://platform.axway.com/user/";

  let requestorEmail = data.request.body.requestorEmail;
  let requestorEmailUrl = "mailto:"+data.request.body.requestorEmail;

  // console.log(approverUrl);

  let body = {
    "@type": "MessageCard",
    "@context": "http://schema.org/extensions",
    "themeColor": "0076D7",
    "summary": "API Subscription Approval Requested Processed",
    "sections": [{
        "activityTitle": "API Subscription Approval Requested Processed",
        "activitySubtitle": "The request below has been processed as "+data.request.body.approvalStatus,
        "markdown": true,
        "facts": [{
            "name": "Catalog Item",
            // "value": data.request.body.payload.consumerInstance.name
          	"value": "["+catalogItemName+"]("+catalogItemUrl+")"
        },{
            "name": "Subscription",
            "value": "["+subscriptionName+"]("+subscriptionUrl+")"
        },{
            "name": "Requestor Name",
            "value": "["+requestorName+"]("+requestorUrl+")"
        },{
            "name": "Requestor Email",
            "value": "["+requestorEmail+"]("+requestorEmailUrl+")"
        }],
    }]
};

  return {url,body};"
